import React from 'react'

export default function CartView() {

    let cartData = JSON.parse(localStorage.getItem("Cart"));
    console.log("cartData:",cartData);

  return (
    <>
    
    <div class="container mt-4">
    <h2>Your Shopping Cart</h2>

        
        {cartData.map((data,i)=>{

            var sum = 0;
            sum = sum + product.Cost;
            return <div class="cart-item row">

                <div class="col-md-1">
                    <img src="@product.ImageUrl" style="height:100px;width:auto;" alt="Product Image" class="product-image"/>
                </div>
                <div class="col-md-11">
                    <table>
                        <tr>
                            <th>Product Name</th>
                            <th>Price</th>
                            <th>Category</th>
                            <th>Size</th>
                            <th>Action</th>
                        </tr>
                        <tr>
                            <td>@product.Name</td>
                            <td>@product.Cost</td>
    
                            <td>@categoryName</td>
                            <td>s</td>

                            <td>
                                @using (Html.BeginForm("RemoveFromCart", "Order", FormMethod.Get))
                                {
                                // <form method="post" action="@Url.Action("RemoveFromCart", "Order")">
                                    <button type="submit" name="id" class="remove-button" value="@product.ProductId">Remove</button>
                                    // </form>
                                }
                            </td>
                        </tr>
                    </table>
                </div>
            </div>
        })}


    {/* <div class="cart-subtotal mt-3">
        <h3>Cart Subtotal:@sum Rs</h3>
    </div>

