        <form method="post" action="@Url.Action("PlaceOrder", "Order")">
            <input type="hidden" name="Amount" value="@sum" />
            <button class="place-order-button" onclick="PlaceOrder()">Place Order</button>
        </form>
    </div> */}
</div>
    </>
  )
}
